DROP TABLE IF EXISTS pages;
CREATE TABLE pages(id INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, url TEXT NOT NULL, is_scraping BOOLEAN, created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP, updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP);
DROP TABLE IF EXISTS links CASCADE;
CREATE TABLE links(id INTEGER GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, page_id INTEGER NOT NULL, url TEXT NOT NULL, created_at TIMESTAMP  DEFAULT CURRENT_TIMESTAMP, updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP, CONSTRAINT FK_Pages FOREIGN KEY(page_id) REFERENCES pages(id));